import React, { useState } from "react";
import "./PatientDashboard.css";

const PatientDashboard = () => {
  const [activeTab, setActiveTab] = useState("createProfile"); // Active tab state
  const [profileFormData, setProfileFormData] = useState({
    PatientID: "",
    PatientName: "",
    MobileNo: "",
    Email: "",
    Password: "",
    BloodGroup: "",
    Gender: "",
    Age: "",
    Address: "",
  });
  const [profileData, setProfileData] = useState(null); // Stores the submitted profile data
  const [appointmentFormData, setAppointmentFormData] = useState({
    AppointmentID: "",
    PatientID: "",
    DoctorID: "",
    Date: "",
    TimeSlot: "",
    Remark: "",
  });
  const [appointmentHistory, setAppointmentHistory] = useState([]); // Stores appointments

  // Handle input changes for forms
  const handleFormChange = (e, formSetter) => {
    const { name, value } = e.target;
    formSetter((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  // Handle profile form submission
  const handleProfileSubmit = (e) => {
    e.preventDefault();
    console.log("Profile Data Submitted:", profileFormData); // Log data
    alert(`Profile Data Submitted:\n${JSON.stringify(profileFormData, null, 2)}`);
    setProfileData(profileFormData); // Save submitted profile
    setProfileFormData({
      PatientID: "",
      PatientName: "",
      MobileNo: "",
      Email: "",
      Password: "",
      BloodGroup: "",
      Gender: "",
      Age: "",
      Address: "",
    }); // Reset form
    setActiveTab("patientProfile");
  };

  // Handle appointment form submission
  const handleAppointmentSubmit = (e) => {
    e.preventDefault();
    console.log("Appointment Data Submitted:", appointmentFormData); // Log data
    alert(`Appointment Data Submitted:\n${JSON.stringify(appointmentFormData, null, 2)}`);
    setAppointmentHistory([...appointmentHistory, appointmentFormData]); // Add to history
    setAppointmentFormData({
      AppointmentID: "",
      PatientID: "",
      DoctorID: "",
      Date: "",
      TimeSlot: "",
      Remark: "",
    }); // Reset form
    setActiveTab("appointmentHistory");
  };

  return (
    <div className="patient-dashboard-container">
      {/* Sidebar for navigation */}
      <div className="patient-sidebar">
        <div className="sidebar-header">
          <h2>Dashboard</h2>
        </div>
        <div className="sidebar-links">
          <button
            onClick={() => setActiveTab(profileData ? "patientProfile" : "createProfile")}
            className="sidebar-button"
          >
            <i className="fas fa-user"></i> {profileData ? "View Profile" : "Create Profile"}
          </button>
          <button onClick={() => setActiveTab("bookAppointment")} className="sidebar-button">
            <i className="fas fa-calendar-plus"></i> Book Appointment
          </button>
          <button onClick={() => setActiveTab("appointmentHistory")} className="sidebar-button">
            <i className="fas fa-history"></i> Appointment History
          </button>
        </div>
      </div>

      {/* Main content area */}
      <div className="patient-main-content">
        <header className="patient-dashboard-header">
          <h1>Patient Dashboard</h1>
        </header>
        <div className="patient-content-container">
          {/* Create/Edit Profile */}
          {activeTab === "createProfile" && (
            <div className="card profile-form">
              <h2>Create Profile</h2>
              <form onSubmit={handleProfileSubmit}>
                {Object.keys(profileFormData).map((key) => (
                  <input
                    key={key}
                    type={key === "Email" ? "email" : "text"}
                    name={key}
                    placeholder={key.replace(/([A-Z])/g, " $1")}
                    value={profileFormData[key]}
                    onChange={(e) => handleFormChange(e, setProfileFormData)}
                    required
                  />
                ))}
                <button type="submit">Submit</button>
              </form>
            </div>
          )}

          {/* View Profile */}
          {activeTab === "patientProfile" && profileData && (
            <div className="card profile-info">
              <h2>Patient Profile</h2>
              {Object.entries(profileData).map(([key, value]) => (
                <p key={key}>
                  <strong>{key.replace(/([A-Z])/g, " $1")}:</strong> {value}
                </p>
              ))}
            </div>
          )}

          {/* Book Appointment */}
          {activeTab === "bookAppointment" && (
            <div className="card appointment-form">
              <h2>Book Appointment</h2>
              <form onSubmit={handleAppointmentSubmit}>
                {Object.keys(appointmentFormData).map((key) => (
                  <input
                    key={key}
                    type="text"
                    name={key}
                    placeholder={key.replace(/([A-Z])/g, " $1")}
                    value={appointmentFormData[key]}
                    onChange={(e) => handleFormChange(e, setAppointmentFormData)}
                    required
                  />
                ))}
                <button type="submit">Book Appointment</button>
              </form>
            </div>
          )}

          {/* Appointment History */}
          {activeTab === "appointmentHistory" && appointmentHistory.length > 0 && (
            <div className="card appointment-history">
              <h2>Appointment History</h2>
              <ul>
                {appointmentHistory.map((appointment, index) => (
                  <li key={index}>
                    {Object.entries(appointment).map(([key, value]) => (
                      <p key={key}>
                        <strong>{key.replace(/([A-Z])/g, " $1")}:</strong> {value}
                      </p>
                    ))}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PatientDashboard;
