import React, { useState,useEffect } from "react";
import "./Doctordashboard.css";
import axios from "axios";

const Doctordashboard = () => {
  const [activeTab, setActiveTab] = useState(""); // To toggle between tabs
  const [profileData, setProfileData] = useState(null); // Store doctor profile
  const [availabilityList, setAvailabilityList] = useState([]); // Store doctor's availability
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [profileFormData, setProfileFormData] = useState({
    doctorId: "",
    doctorName: "",
    speciality: "",
    location: "",
    hospitalName:"",
    mobileNo: "",
    email: "",
    experience: "",
    //profilePhoto: null,
  });

  const [availabilityFormData, setAvailabilityFormData] = useState({
    availabilityId: "",
    doctor: "",
    startDate: "",
    endDate: "",
    fromTime: "",
    toTime: "",
    index: null, // Track the index of the item to edit
  });

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  const handleProfileFormChange = (e) => {
    const { name, value } = e.target;
    setProfileFormData({ ...profileFormData, [name]: value });
  };

  const handleAvailabilityFormChange = (e) => {
    const { name, value } = e.target;
    setAvailabilityFormData({ ...availabilityFormData, [name]: value });
  };


  //API connection.................................
  const handleProfileSubmit = async (e) => {
    e.preventDefault();
  
    setProfileData(profileFormData); // Save profile data
    console.log("Doctor Profile Data:", profileFormData); // Log profile data to console
  
    // Make an Axios POST request to submit the profile data to the backend
    try {
      const response = await axios.post('http://localhost:8000/addDoctor', profileFormData);
      console.log("Response from backend:", response.data);
      
      // Optionally, you can handle success or redirect here
      // For example, clearing the form or showing a success message
  
      setProfileFormData({
        doctorId: "",
        doctorName: "",
        speciality: "",
        location: "",
        hospitalName: "",
        mobileNo: "",
        email: "",
        experience: "",
        // profilePhoto: null,
      });
  
      setActiveTab("doctorProfile"); // After saving, show the profile info
  
    } catch (error) {
      console.error("Error submitting profile data:", error);
      // Handle the error accordingly (e.g., show an error message)
    }
  };
  


  const handleAddAvailabilitySubmit = (e) => {
    e.preventDefault();
    if (availabilityFormData.index !== null) {
      // Edit existing availability
      const updatedList = [...availabilityList];
      updatedList[availabilityFormData.index] = availabilityFormData;
      setAvailabilityList(updatedList);
      console.log("Updated Availability:", availabilityFormData); // Log updated availability to console
    } else {
      // Add new availability
      setAvailabilityList([...availabilityList, availabilityFormData]);
      console.log("Added Availability:", availabilityFormData); // Log new availability to console
    }
    setAvailabilityFormData({
      availabilityId: "",
      doctor: "",
      startDate: "",
      endDate: "",
      fromTime: "",
      toTime: "",
      index: null,
    });
    setActiveTab(""); // Reset view
  };

  const handleEditProfile = () => {
    setProfileFormData(profileData); // Load existing profile data into the form
    setActiveTab("createProfile"); // Open the profile creation form to edit
  };

  const handleDeleteProfile = () => {
    if (window.confirm("Are you sure you want to delete your profile?")) {
      setProfileData(null);
      alert("Profile deleted successfully!");
    }
  };

  const handleEditAvailability = (index) => {
    setAvailabilityFormData({
      ...availabilityList[index],
      index: index, // Track the index of the availability to be edited
    });
    setActiveTab("addAvailability"); // Open the availability form to edit
  };

  const handleDeleteAvailability = (index) => {
    if (window.confirm("Are you sure you want to delete this availability?")) {
      const updatedList = availabilityList.filter((_, i) => i !== index);
      setAvailabilityList(updatedList);
      alert("Availability deleted successfully!");
    }
  };

//


  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button onClick={() => setActiveTab("Dashboard")}>
          <i className="fas fa-tachometer-alt"></i> Dashboard
        </button>

        {profileData ? (
          <button onClick={() => setActiveTab("doctorProfile")}>
            <i className="fas fa-user-md"></i> Doctor Profile
          </button>
        ) : (
          <button onClick={() => setActiveTab("createProfile")}>
            <i className="fas fa-user-plus"></i> Create Profile
          </button>
        )}

        {availabilityList.length > 0 ? (
          <button onClick={() => setActiveTab("viewAvailability")}>
            <i className="fas fa-calendar-check"></i> View Availability
          </button>
        ) : (
          <button onClick={() => setActiveTab("addAvailability")}>
            <i className="fas fa-calendar-plus"></i> Add Availability
          </button>
        )}

        <button onClick={() => setActiveTab("viewAppointments")}>
          <i className="fas fa-clipboard-list"></i> View Appointments
        </button>

        <button className="logout-button" onClick={handleLogout}>
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1 className="dashboard-heading">Welcome to Doctor Dashboard</h1>

        {/* Dashboard Information */}
        {activeTab === "Dashboard" && (
          <div className="dashboard-content">
            <div className="dashboard-box" style={{ backgroundColor: "#4CAF50" }}>
              <i className="fas fa-calendar-alt"></i>
              <h3>Total Appointments</h3>
              <p>0</p>
            </div>

            <div className="dashboard-box" style={{ backgroundColor: "#f44336" }}>
              <i className="fas fa-toTimes-circle"></i>
              <h3>Canceled Appointments</h3>
              <p>0</p>
            </div>

            <div className="dashboard-box" style={{ backgroundColor: "#2196F3" }}>
              <i className="fas fa-check-circle"></i>
              <h3>Completed Appointments</h3>
              <p>0</p>
            </div>
          </div>
        )}

        {/* Create Profile Form or Profile Info */}
        {activeTab === "createProfile" && (
          <div className="centered-form">
            <form onSubmit={handleProfileSubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {profileData ? "Edit Doctor Profile" : "Create Doctor Profile"}
              </h2>
              <input
                type="text"
                name="doctorName"
                placeholder="Name"
                value={profileFormData.doctorName}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="doctorId"
                placeholder="Doctor ID"
                value={profileFormData.doctorId}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="speciality"
                placeholder="Specialization"
                value={profileFormData.speciality}
                onChange={handleProfileFormChange}
                required
              />

              <input
                type="text"
                name="location"
                placeholder="Location"
                value={profileFormData.location}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="hospitalName"
                placeholder="Hospital Name"
                value={profileFormData.hospitalName}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="mobileNo"
                placeholder="Mobile No"
                value={profileFormData.mobileNo}
                onChange={handleProfileFormChange}
                required
              />
              <input
                type="text"
                name="email"
                placeholder="Email"
                value={profileFormData.email}
                onChange={handleProfileFormChange}
                required
              />
               <input
                type="text"
                name="experience"
                placeholder="Experience (in years)"
                value={profileFormData.experience}
                onChange={handleProfileFormChange}
                required
              />
            {/*  <input 
                type="file"
                name="profilePhoto"
                onChange={(e) =>
                  setProfileFormData({ ...profileFormData, profilePhoto: e.target.files[0] })
                }
              /> */} 
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {/* Doctor Profile Info */}
        {activeTab === "doctorProfile" && profileData && (
          <div className="centered-form">
            <h2>Doctor Profile</h2>
            <p><strong>Name:</strong> {profileData.doctorName}</p>
            <p><strong>Doctor ID:</strong> {profileData.doctorId}</p>
            <p><strong>Specialization:</strong> {profileData.speciality}</p>
            <p><strong>Location:</strong> {profileData.location}</p>
            <p><strong>Hospital Name:</strong> {profileData.hospitalName}</p>
            <p><strong>Mobile No:</strong> {profileData.mobileNo}</p>
            <p><strong>Email:</strong> {profileData.email}</p>
            <p><strong>Experience:</strong> {profileData.experience} years</p>
            
            {/*  {profileData.profilePhoto && (
              <img
                src={URL.createObjectURL(profileData.profilePhoto)}
                alt="Profile"
                style={{ width: "150px", borderRadius: "50%" }}
              />
            )} */}
            <div className="profile-actions">
              <button onClick={handleEditProfile}>Edit Profile</button>
              <button onClick={handleDeleteProfile} style={{ background: "red", color: "white" }}>
                Delete Profile
              </button>
            </div>
          </div>
        )}

        {/* Add/Edit Availability Form */}
        {activeTab === "addAvailability" && (
          <div className="centered-form">
            <form onSubmit={handleAddAvailabilitySubmit}>
              <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
                {availabilityFormData.index !== null ? "Edit Availability" : "Add Availability"}
              </h2>
              <input
                type="text"
                name="doctor"
                placeholder="Doctor ID"
                value={availabilityFormData.doctor}
                onChange={handleAvailabilityFormChange}
                required
              />
            n<input
                type="text"
                name="availabilityId"
                placeholder="Availability ID"
                value={availabilityFormData.availabilityId}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="startDate"
                value={availabilityFormData.startDate}
                onChange={handleAvailabilityFormChange}
                required
              />
              <input
                type="date"
                name="endDate"
                value={availabilityFormData.endDate}
                onChange={handleAvailabilityFormChange}
                required
              />
                <label htmlFor="fromTime">Time Form:</label>
              <input
                type="time" 
                name="fromTime"
                placeholder="fromTime"
                value={availabilityFormData.fromTime}
                onChange={handleAvailabilityFormChange}
                required
              />
                <label htmlFor="toTime">To:</label>
              <input
                type="time" 
                name="toTime"
                placeholder="toTime"
                value={availabilityFormData.toTime}
                onChange={handleAvailabilityFormChange}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        )}

        {/* View Availability List */}
        {activeTab === "viewAvailability" && availabilityList.length > 0 && (
          <div className="availability-list">
            <h2>Doctor Availability</h2>
            {availabilityList.map((availability, index) => (
              <div key={index} className="availability-item">
                <p><strong>Doctor ID:</strong> {availability.doctor}</p>
                <p><strong>Availability ID:</strong> {availability.availabilityId}</p>
                <p><strong>Start Date:</strong> {availability.startDate}</p>
                <p><strong>End Date:</strong> {availability.endDate}</p>
                <p><strong>Time From:</strong> {availability.fromTime}</p>
                <p><strong>To:</strong> {availability.toTime}</p>
                <div className="availability-actions">
                  <button onClick={() => handleEditAvailability(index)}>Edit</button>
                  <button onClick={() => handleDeleteAvailability(index)} style={{ background: "red", color: "white" }}>
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Doctordashboard;
