import React, { useState } from "react";
import "./DoctorDashboard.css";
import DoctorProfile from "./DoctorProfile";
import AvailabilityForm from "./AvailabilityForm";
import ViewAppointments from "./ViewAppointments";

const DoctorDashboard = () => {
  const [activeTab, setActiveTab] = useState("Dashboard");

  const handleLogout = () => {
    alert("Logged out successfully!");
    window.location.href = "/login"; // Redirect to login page
  };

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className="sidebar">
        <button onClick={() => setActiveTab("Dashboard")}>Dashboard</button>
        <button onClick={() => setActiveTab("DoctorProfile")}>Doctor Profile</button>
        <button onClick={() => setActiveTab("AvailabilityForm")}>Manage Availability</button>
        <button onClick={() => setActiveTab("ViewAppointments")}>View Appointments</button>
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </div>

         {/* Main Content */}
         <div className="main-content">
        <h1 className="dashboard-heading">Welcome to Doctor Dashboard</h1>

        {/* Dashboard Information */}
        {activeTab === "Dashboard" && (
          <div className="dashboard-content">
            <div className="dashboard-box" style={{ backgroundColor: "#4CAF50",color:"black" }}>
              <i className="fas fa-calendar-alt"></i>
              <h3>Total Appointments</h3>
              <p>0</p>
            </div>

            <div className="dashboard-box" style={{ backgroundColor: "#f44336" }}>
              <i className="fas fa-toTimes-circle"></i>
              <h3>Canceled Appointments</h3>
              <p>0</p>
            </div>

            <div className="dashboard-box" style={{ backgroundColor: "#2196F3" }}>
              <i className="fas fa-check-circle"></i>
              <h3>Completed Appointments</h3>
              <p>0</p>
            </div>
          </div>
        )}

        {activeTab === "DoctorProfile" && <DoctorProfile />}
        {activeTab === "AvailabilityForm" && <AvailabilityForm />}
        {activeTab === "ViewAppointments" && <ViewAppointments />}
      </div>
    </div>
  );
};

export default DoctorDashboard;
