import React, { useState } from "react";
import axios from "axios";
import "./DoctorProfile.css";

const DoctorProfile = () => {
  const [profileData, setProfileData] = useState(null);
  const [formData, setFormData] = useState({
    doctorId: "",
    doctorName: "",
    speciality: "",
    location: "",
    mobileNo: "",
    email: "",
    experience: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/doctor/addDoctor', formData);
      alert("Profile saved successfully!");
      setProfileData(response.data);
    } catch (error) {
      console.error("Error saving profile:", error);
    }
  };

  return (
    <div className="doctorPro">
      <h2>{profileData ? "Edit Doctor Profile" : "Create Doctor Profile"}</h2>
      <form onSubmit={handleSubmit}>
        <input name="doctorName" placeholder="Name" value={formData.doctorName} onChange={handleChange} required />
        <input name="doctorId" placeholder="Doctor ID" value={formData.doctorId} onChange={handleChange} required />
        <input name="speciality" placeholder="Specialization" value={formData.speciality} onChange={handleChange} required />
        <input name="location" placeholder="Location" value={formData.location} onChange={handleChange} required />
        <input name="mobileNo" placeholder="Mobile No" value={formData.mobileNo} onChange={handleChange} required />
        <input name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input name="experience" placeholder="Experience" value={formData.experience} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>

      {profileData && (
        <div>
          <h3>Doctor Profile</h3>
          <p><strong>Name:</strong> {profileData.doctorName}</p>
          <p><strong>Email:</strong> {profileData.email}</p>
        </div>
      )}
    </div>
  );
};

export default DoctorProfile;
